import React, { useState } from 'react'
import { history} from 'react-router-dom'
//import axios from 'axios';
//import moment from 'moment';

const Form = () =>{
    const [user, setUser] = useState({
        examId :" ", name:" ", grade :" ", subject:" ",date :" ", timeStart:" ", timeEnd :" ", notice:" "
    });

    let name,value;

    const handleInput = (e) => {
        console.log(e);
        name = e.target.name;
        value = e.target.value;

        setUser({...user,[name]:value});
    }
      
    const PostData =async(e) =>{
        e.preventDefault();
        const { examId, name, grade, subject, date, timeStart, timeEnd, notice } =user;
        const res = await fetch("/add",{
            method:"POST",
            headers:{
                "Content-Type": "application/json"
            },
           body:JSON.stringify({
            examId, name, grade, subject, date, timeStart, timeEnd, notice
           })

        });
    
        const data= await data.json();
        if(data.status ===200 || !data){
            window.alert("Exam add unsuccesfull");
            console.log("Exam add unsuccesfull");
        }else{
            window.alert("Exam add succesfull");
            console.log("Exam add succesfull");

            history.push("/home");
        }


    }

        return (
            <div>
              
                   <form method ="POST">
                <div className="container mt-3 text-start">

                    <div className="row my-1">
                        <div className="col col-2">
                            <label htmlFor="examId"> Exam ID </label>
                        </div>
                        <div className="col col-3">
                            <input className="form-control" id="examId"
                            type='text' 
                            value= {user.examId}
                            onChange={handleInput}
                            />
                        </div>
                    </div>
                    <div className="row my-1">
                        <div className="col col-2">
                            <label htmlFor="name"> Exam Name </label>
                        </div>
                        <div className="col col-3">
                            <input className="form-control" id="name"
                            type='text' 
                            value= {user.name}
                            onChange={handleInput}
                            />
                        </div>
                    </div>
                    <div className="row my-1">
                        <div className="col col-2">
                            <label htmlFor="grade"> Grade </label>
                        </div>
                        <div className="col col-3">
                            <input className="form-control" id="grade"
                            type='number' 
                            value= {user.grade}
                            onChange={handleInput}
                            />
                        </div>
                    </div>
                    
                    <div class="row my-1">
                        <div class="col col-2">
                            <label htmlFor="subject" > Subject </label>
                        </div>
                        <div class="col col-3">
                            <input className="form-control" id="subject"
                            type='text' 
                            value= {user.subject}
                            onChange={handleInput}
                             />
                        </div>
                    </div>

                    <div className="row my-1">
                        <div className="col col-2">
                            <label htmlFor="date"> Date </label>
                        </div>
                        <div className="col col-2">
                            <input className="form-control" id="date"
                            type='text' 
                            value= {user.date}
                            onChange={handleInput}
                             />
                        </div>
                    </div>

                    <div className="row my-1">
                        <div className="col col-2">
                            <label> Time </label>
                        </div>
                        <div className="col col-auto">
                            <label htmlFor="timeStart">From</label>
                        </div>
                        <div className="col col-auto">
                            <input className="form-control" id="timeStart"
                            type='time' 
                            value= {user.timeStart}
                            onChange={handleInput}
                            />
                        </div>
                        <div className="col col-auto">
                            <label htmlFor="timeEnd">To</label>
                        </div>
                        <div className="col col-auto">
                            <input className="form-control" id="timeEnd"
                            type='time' 
                            value= {user.timeEnd}
                            onChange={handleInput}
                            />
                        </div>
                    </div>

                    <div className="row my-1">
                        <div className="col col-2">
                            <label htmlFor="notice"> Notice </label>
                        </div>
                        <div className="col col-6">
                            <textarea className="form-control" rows="5" id="notice"
                                type='time' 
                                value= {user.notice}
                            onChange={handleInput}
                            />
                        </div>
                    </div>

                    <div className="row my-2">
                        <div className="col col-2"></div>
                        <div className="col col-3">
                            <button type="submit" className="btn btn-primary w-100 p-3 " onclick={PostData}>
                                SUBMIT
                            </button>
                        </div>
                        
                    </div>
                </div>
                </form>
                
            </div>
        )
}
export default Form;
